import styled from 'styled-components';

export const PlaylistItemWrapper = styled.div`
    display: flex;
    align-items: center;
    padding-left: 15px;
    height: 28px;
    cursor: pointer;
    justify-content: space-around;
    margin-top: 2px;

    &:hover{
        background-color: rgba(0, 0, 0, 0.4);
        color: #fff;
    }

    &.active {
        background-color: rgba(0, 0, 0, 0.4);
    }

    .song-name {
        width: 250px;
        height: 28px;
        text-align: left;
        line-height: 28px;

    }
`
