import {PlayerbarWrapper,PlayerInfo,Control,Operator} from './style';
import {Slider,Tooltip} from 'antd';
import {useDispatch,useSelector,shallowEqual} from 'react-redux';
import {useEffect} from 'react';
import {getSongDetailAction} from '../store/actionCreator';

function AppPlayerBar(){

    //发送网络请求，请求歌曲详情
    // const dispatch = useDispatch();
    // useEffect(()=>{
    //     dispatch(getSongDetailAction(100));
    // },[dispatch]);

    // 获取store中的数据
    const {currentSong} = useSelector((state)=>({
        currentSong:state.player.currentSong
    }),shallowEqual);  

    console.log(currentSong);

    return (
        <PlayerbarWrapper className='sprite_player'>
            <div className='content'>
                <Control isPlaying={false}>
                    <button className='sprite_player pre'></button>
                    <button className='sprite_player play'></button>
                    <button className='sprite_player next'></button>
                </Control>
                <PlayerInfo>
                    <a href='#/discover/song' className='image'>
                        {/* <img src/> */}
                    </a>
                    <div className='play-detail'>
                        <div className='song-info'>
                            <a className='song-name' href='#/discover/song'>XXX</a>
                            <a className='song-author' href='#/author'>XXX</a>
                        </div>
                        <Slider/>
                        <div className='song-time'>
                            <span className='now-time'>XXX /</span>
                            <span className='total-time'> XXX</span>
                        </div>
                    </div>
                </PlayerInfo>
                <Operator>
                    {/* 左侧暂时不实现 */}
                    <div className='left'></div>
                    <div className='right'>
                        <Tooltip title='调节音量'>
                            <button className='sprite_player btn volume'></button>
                        </Tooltip>
                        <Tooltip
                            title={['顺序播放','随机播放','单曲循环'].filter((item,index)=>(
                                index === 0
                            ))}
                        >
                            <button className='sprite_player btn loop'></button>
                        </Tooltip>
                        <Tooltip title='播放列表'>
                            <button className='sprite_player btn playlist'>
                                <span>9</span>
                            </button>
                        </Tooltip>
                    </div>
                </Operator>
            </div>
        </PlayerbarWrapper>
    );
}

export default AppPlayerBar;
