import { Form, Input, Button, Checkbox, message } from 'antd'
import propTypes from 'prop-types';
import './style.css';
import { getParseLoginState, getMatchReg } from '@/utils/format-utils'

function ThemeLoginForm(props){

    // 拿到"登录的方式"
    const { loginState } = props

    return (
        // 只写了登录页面
        <Form
            style={{display: loginState !== 'register' ? 'block' : 'none'}}
        >
            <Form.Item
                label='手机登录'
                name='username'
            >
                <Input />
            </Form.Item>
            <Form.Item
                label='密码'
                name="password"
            >
                <Input.Password />
            </Form.Item>
            <div className='textAlignRight'>
                <Checkbox className='mr80' defaultChecked={true}>自动登录</Checkbox>
                <span className='forgetPwd'>忘记密码?</span>
            </div>
            <Form.Item
                // wrapperCol= { {span:30 }}
            >
                <Button
                    type="primary"
                    htmlType="submit"
                    size="middle"
                    block
                    shape="round"
                >
                    登录
                </Button>
            </Form.Item>

        </Form>
    );
}

ThemeLoginForm.propTypes = {
    loginState: propTypes.string,
}

ThemeLoginForm.defaultProps = {
    loginState: 'phone',
}

export default ThemeLoginForm;
