import * as actionTypes from './actionTypes'

const defaultState = {
    topBanners: []
}

function reducer(state = defaultState, action){
    console.log(state);
    switch (action.type) {
        case actionTypes.CHANGE_TOP_BANNER:
            return {
                topBanners:action.topBanners.banners
            }  
        default:
            return state

    }
}

export {
    reducer
}
