import styled from 'styled-components'

export const SongCoverWrapper = styled.a`
    width: ${props => props.width+'px'};
    .cover-wrapper{
        width: 140px;
        height: 140px;
        position: relative;

        img{
            width: 100%;
        }
    }

    .cover-title{
        font-size: 14px;
        color: #000;
        margin-top: 5px;
    }




`
