import {SongItemWrapper} from './style';
import propTypes from 'prop-types';
import {NavLink} from 'react-router-dom';
import {getSongDetailAction} from '@/pages/player/store/actionCreator';
import {useDispatch} from 'react-redux';


function SongItem(props){

    /*************** 播放音乐 *************/
    const dispatch = useDispatch()

    function playMusic(e,isTo=false){
        // 如果不跳转，禁止跳转
        if (!isTo) e.preventDefault()
        dispatch(getSongDetailAction(props.songId))
    }


    return (
        <SongItemWrapper>
            <div className='song-item rank-count'>{props.currentRanking}</div>
            { coverPic && (
                <NavLink
                to="/discover/song"
                className="song-item"
                onClick={(e) => playMusic(e, true)}
                >
                    <img src={getSizeImage(coverPic, 50)} alt="" />
                </NavLink>
            )}
        </SongItemWrapper>
    );
}

SongItem.propTypes = {
    currentRanking: propTypes.number.isRequired,
    coverPic: propTypes.string,
    duration: propTypes.string.isRequired,
    singer: propTypes.string.isRequired,
    songId: propTypes.number.isRequired,
    className: propTypes.string,
    songName: propTypes.string.isRequired,
}

export default SongItem;
