import styled from 'styled-components';

// 控制header局部样式
export const HeaderWrapper = styled.div`
    background-color: #242424;

    .content{
        display: flex;
        justify-content: space-between;
        color: #fff;
    }

    .redline{
        height: 5px;
        background-color: #c20b0b;
    }
`

export const HeaderLeft = styled.div`
    display: flex;

    .logo {
        display: inline-block;
        width: 176px;
        height: 69px;
        background-position: 0 0;
    }

    .header-group{
        display: flex;
        .header-item{
            color: #ccc;
            font-size: 14px;
            text-align: center;
            height: 69px;
            line-height: 69px;
            padding: 0 20px;

            /* 鼠标悬浮背景高亮 */
            &:hover{
                text-decoration: none;
                background-color: #000;
            }

            /* hot图标 */
            :last-of-type{
                position: relative;
                ::after{
                    /* 添加伪元素 */

                }
            }
        }
    }
`
