import Draggable from 'react-draggable';
import {Modal,message,Button} from 'antd';
import {LoginWrapper,LoginLeft,LoginRight} from './style';
import LoginIcon from 'components/theme-controls-icon/login';
import {useSelector,shallowEqual,useDispatch} from 'react-redux';
import {changeIsVisible} from './store/actionCreator';
import {useState} from 'react';

function ThemeLogin(){

    // 获取store中的数据
    const {isVisible} = useSelector(state=>({
        isVisible:state.loginState.isVisible
    }),shallowEqual);

    // 组件内状态
    const [disabled, setDisabled] = useState(true)
    const [loginState, setLoginState] = useState('default') // 默认状态显示


    const dispatch = useDispatch();


    /********* 取消 ********/
    function handleCancel(){
        // 关闭对话框
        dispatch(changeIsVisible(false));
    }


    /********* 登录模式 ********/
    function handleLogin(loginMode){
        switch(loginMode){
            case 'phone':
                setLoginState('phone');
                break;
            case 'email':
                setLoginState('email');
                break;
            case 'register':
                setLoginState('register');
                break;
            default:
        }
    }



    // JSX
    const defaultWrapperContent = (
        <LoginWrapper>
            <LoginLeft>
                <div className='login-content'>
                    <div className='login-bg'></div>
                    <Button
                        type="primary"
                        className='btn'
                        onClick={()=>{handleLogin('phone')}}
                    >
                        手机号登录
                    </Button>
                    <Button
                        type='ghost'
                        className='btn'
                    >
                        注册
                    </Button>
                </div>
            </LoginLeft>
            <LoginRight>
                <div className='icons-wrapper'>
                    <LoginIcon
                        onClick={() => message.warn('暂未开通')}
                        position="-150px -670px"
                        description="微信登录"
                    />
                    <LoginIcon
                        onClick={() => message.warn('暂未开通')}
                        position="-190px -670px"
                        description="QQ登录"
                    />
                    <LoginIcon
                        onClick={() => message.warn('暂未开通')}
                        position="-231px -670px"
                        description="微博登录"
                    />
                    <LoginIcon
                        onClick={() => message.warn('稍后')}
                        position="-271px -670px"
                        description="网易邮箱登录"
                    />

                </div>
            </LoginRight>
        </LoginWrapper>
    );


    return (
        <div>
            <Modal
                centered={true}
                footer={null}
                visible={isVisible}
                title={
                    <div
                        style={{width: '100%',cursor:'move',color:'#ccc'}}
                    >
                        登&nbsp;录
                    </div>
                }
                onCancel={handleCancel}
            >
                {defaultWrapperContent}
            </Modal>
        </div>
    );
}

export default ThemeLogin;
