import styled from 'styled-components'

export const SongCoverWrapper = styled.a`
    display: inline-block;
    width: ${props => props.width+'px'};
    margin: 20px 0 20px 0;
    .cover-wrapper{
        width: 140px;
        height: 140px;
        position: relative;

        .bottom-bar{
            position: absolute;

        }

    }

    .cover-title{
        font-size: 14px;
        color: #000;
        margin-top: 5px;
    }

    .cover-source{
        color: #666;
    }




`
