import * as actionType from './actionTypes';

const defaultState = {
    currentSong: {}
}

function reducer(state=defaultState,action){
    switch(action.type){
        case(actionType.CHANGE_CURRENT_SONG):
            return ({
                ...state,
                currentSong:action.currentSong
            });
    }
}

export default reducer;
