import styled from 'styled-components';

export const NavBarWrapper = styled.div`
    width: 100%;
    height: 35px;
    background-color: #c20c0c;
`

export const CategoryList = styled.ul`
    width: 1100px;
    margin: 0 auto;
    display: flex;
    padding-left: 180px;

    .item{
        height: 35px;
        line-height: 35px;
        padding: 5px 32px;
        a {
            display: inline-block;
            color: #fff;
            border-radius: 20px;
            &:hover,
            &.menu-active {
                text-decoration: none;
                background-color: #9b0909;
            }
        }
    }
`
